import React, {useState} from 'react'
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import { SecurityUpdateGoodSharp } from '@mui/icons-material';



const TodoList = ({todos, setTodos}) => {
  const [clicked, setClicked] = useState(false)

  const handleClick = () => {
    setClicked(curr=> !curr)
  }

  const handleDelete = ({id}) => {
    setTodos(todos.filter(todo=> todo.id !== id))
  }
  return (
    <div className='items-container'>
      {todos.map((item, i) =>
      <div key={i} className="todo-item">
        <span onClick={handleClick}>{ clicked ?<CheckCircleIcon/> : <CheckCircleOutlineIcon/>}</span>

        <p className={clicked ? 'item-completed' : 'item-waiting'}>{item.todo}</p>
        <span onClick={()=> handleDelete(todo)}><DeleteOutlineIcon/></span>
        </div>
        )}
    </div>
  )
}
export default TodoList
