import React, {useState} from 'react'
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';



const TodoList = ({todo}) => {
  const [clicked, setClicked] = useState({})

  const handleClick = (i) => {
    setClicked(state => ({
    ...state, // <-- copy previous state
    [i]: !state[i] // <-- update value by index key
  }));
  }
  return (
    <div className='items-container'>
      {todos.map((item, i) =>
      <div key={i} className="todo-item">
         <span onClick={handleClick(i)}>{ clicked === true ?<CheckCircleIcon/> : <CheckCircleOutlineIcon/>}</span>

        <p className={clicked ? 'item-completed' : 'item-waiting'}>{item.todo}</p>
        <span><DeleteOutlineIcon/></span>
        </div>
        )}
    </div>
  )
}
export default TodoList
