import React, {useState} from 'react'
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';


const TodoItem = ({todo, handleDelete}) => {

  const [clicked, setClicked] = useState(todo.clicked);

  const handleClick = (e) => {
    setClicked(!clicked)
  }

  return (
    <div className="todo-item">
      <span
        onClick={handleClick}
        clicked={clicked}
      >
        { clicked ?<CheckCircleIcon/> : <CheckCircleOutlineIcon/>}
      </span>

      <p className={clicked ? 'item-completed' : 'item-waiting'}>{todo}</p>
      <span onClick={()=> handleDelete(todo.id)}><DeleteOutlineIcon/></span>
    </div>
  )
}

export default TodoItem
